DROP PROCEDURE IF EXISTS `account_balance`;
DELIMITER |
CREATE PROCEDURE `account_balance`(
IN `account_id` INT
)
BEGIN
SET @row_number = 0;
SELECT
@row_number := @row_number + 1 AS Sno,
a.account_name AS `Account Name`,
SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Debit,
SUM(CASE WHEN t.credit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Credit,
CONCAT('$', FORMAT(ABS(SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE -t.amount END)), 2),
IF(SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE -t.amount END) < 0, ' Cr', ' Dr')
) AS Balance
FROM
accounts a
JOIN transactions t ON a.account_id IN (t.debit_account_id, t.credit_account_id)
JOIN (SELECT @row_number:=0) r
WHERE
a.group_id = account_id
AND a.is_deleted = 0
and t.is_deleted<>1
GROUP BY
a.account_id, a.account_name
UNION ALL
SELECT
NULL AS Sno,
'Total' AS `Account Name`,
SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Debit,
SUM(CASE WHEN t.credit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Credit,
CONCAT('$', FORMAT(ABS(SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE -t.amount END)), 2),
IF(SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE -t.amount END) < 0, ' Cr', ' Dr')
) AS Balance
FROM
accounts a
JOIN transactions t ON a.account_id IN (t.debit_account_id, t.credit_account_id)
WHERE
a.group_id = account_id
AND a.is_deleted = 0
and t.is_deleted<>1;
END |
