DROP PROCEDURE IF EXISTS `account_statement`;
DELIMITER |
CREATE PROCEDURE `account_statement`(
IN `account_id` INT,
IN `from_date` DATE,
IN `to_date` DATE
)
BEGIN
SELECT
0 AS sno,
'Opening Balance' AS transaction_date,
NULL AS voucher_no,
NULL AS debit_account_name,
NULL AS credit_account_name,
CONCAT(
ROUND(ABS(COALESCE(SUM(CASE WHEN debit_account_id = account_id THEN amount ELSE 0 END), 0) -
COALESCE(SUM(CASE WHEN credit_account_id = account_id THEN amount ELSE 0 END), 0) +
COALESCE(a.opening_balance, 0)), 2),
' ',
CASE WHEN ROUND(SUM(CASE WHEN debit_account_id = account_id THEN amount ELSE -amount END), 2) -
ROUND(SUM(CASE WHEN credit_account_id = account_id THEN amount ELSE -amount END), 2) +
ROUND(COALESCE(a.opening_balance, 0), 2) >= 0
THEN 'dr'
ELSE 'cr'
END
) AS balance,
NULL AS narration
FROM
accounts a
LEFT JOIN
transactions t ON t.debit_account_id = a.account_id OR t.credit_account_id = a.account_id
WHERE
(t.transaction_date IS NULL OR date(t.transaction_date) < from_date)
AND a.account_id = account_id
AND t.is_deleted <> 1
UNION ALL
SELECT
ROW_NUMBER() OVER(ORDER BY t.transaction_date, t.voucher_no) AS sno,
t.transaction_date,
t.voucher_no,
da.account_name AS debit_account_name,
ca.account_name AS credit_account_name,
CONCAT(
ROUND(ABS(COALESCE(SUM(CASE WHEN debit_account_id = account_id THEN amount ELSE amount END), 0)), 2),
' ',
CASE WHEN ROUND(SUM(CASE WHEN debit_account_id = account_id THEN amount ELSE -amount END), 2) >= 0
THEN 'dr' ELSE 'cr' END
) AS balance,
t.narration
FROM
transactions t
INNER JOIN accounts da ON t.debit_account_id = da.account_id
INNER JOIN accounts ca ON t.credit_account_id = ca.account_id
LEFT JOIN accounts a ON da.account_id = a.account_id
WHERE
(t.debit_account_id = account_id OR t.credit_account_id = account_id) AND
t.is_deleted <> 1 AND
t.transaction_date BETWEEN from_date AND to_date
GROUP BY
t.transaction_id
UNION ALL
SELECT
9999999 AS sno,
'Closing Balance' AS transaction_date,
NULL AS voucher_no,
NULL AS debit_account_name,
NULL AS credit_account_name,
CONCAT(
ROUND(ABS(COALESCE(SUM(CASE WHEN debit_account_id = account_id THEN amount ELSE 0 END), 0) -
COALESCE(SUM(CASE WHEN credit_account_id = account_id THEN amount ELSE 0 END), 0) +
COALESCE(a.opening_balance, 0)), 2),
' ',
CASE WHEN ROUND(SUM(CASE WHEN debit_account_id = account_id THEN amount ELSE -amount END), 2) -
ROUND(SUM(CASE WHEN credit_account_id = account_id THEN amount ELSE -amount END), 2) +
ROUND(COALESCE(a.opening_balance, 0), 2) >= 0
THEN 'dr'
ELSE 'cr'
END
) AS balance,
NULL AS narration
FROM
accounts a
LEFT JOIN
transactions t ON t.debit_account_id = a.account_id OR t.credit_account_id = a.account_id
WHERE
(t.transaction_date IS NULL OR date(t.transaction_date) <= to_date)
AND a.account_id = account_id
AND t.is_deleted <> 1;
END |
DELIMITER ;

