DROP PROCEDURE IF EXISTS `CalculateProfitForDateRange`;
DELIMITER |
CREATE PROCEDURE `CalculateProfitForDateRange`(IN start_date DATE, IN end_date DATE)
BEGIN
SELECT
s.stockid,
p.description,
SUM(s.netamount) AS total_sales_net_amount,
SUM(s.qty) AS total_sales_qty,
SUM(LAST_THREE_PURCHASE.total_qty_purchased) AS total_qty_purchased,
SUM(LAST_THREE_PURCHASE.total_purchase_value) AS total_purchase_value,
AVG(LAST_THREE_PURCHASE.total_purchase_value / LAST_THREE_PURCHASE.total_qty_purchased) AS avg_purchase_cost,
AVG(s.netamount / s.qty) AS avg_sale_cost,
(SUM(s.netamount) - AVG(LAST_THREE_PURCHASE.total_purchase_value / LAST_THREE_PURCHASE.total_qty_purchased) * SUM(s.qty)) AS profit
FROM
bill s
JOIN (
SELECT
stockid,
SUM(qty) AS total_qty_purchased,
SUM(netamount) AS total_purchase_value
FROM
pbill
GROUP BY
stockid
ORDER BY
bill_date DESC
LIMIT 3
) LAST_THREE_PURCHASE ON s.stockid = LAST_THREE_PURCHASE.stockid
JOIN products p ON s.stockid = p.autoid
WHERE
s.bill_date >= start_date AND s.bill_date <= end_date
GROUP BY
s.stockid, p.description;
END |
DELIMITER ;

DROP PROCEDURE IF EXISTS `compare_databases`;
DELIMITER |
CREATE PROCEDURE `compare_databases`(
IN `database1` VARCHAR(50),
IN `database2` VARCHAR(50)
)
BEGIN
SELECT CONCAT('ALTER TABLE ', table_name, ' ADD COLUMN ', column_name, ' ',
IF(column_type LIKE '%int%', CONCAT(column_type, ' NOT NULL DEFAULT 0'), column_type), ';') AS result
FROM information_schema.columns
WHERE table_schema = database1 AND table_name IN (SELECT table_name FROM information_schema.tables WHERE table_schema = database2)
AND CONCAT(table_name, '.', column_name) NOT IN (SELECT CONCAT(table_name, '.', column_name) FROM information_schema.columns WHERE table_schema = database2)
UNION
SELECT CONCAT('CREATE TABLE ', table_name, ' LIKE ', database1, '.', table_name, ';') AS result
FROM information_schema.tables
WHERE table_schema = database1 AND table_name NOT IN (SELECT table_name FROM information_schema.tables WHERE table_schema = database2);
END |
DELIMITER ;

