DROP PROCEDURE IF EXISTS `day_inventory_book`;
DELIMITER |
CREATE PROCEDURE `day_inventory_book`(
IN sdate date,
IN edate date
)
BEGIN
select stock_amt,sum(purchase_amt+opening_bal.stock_amt-purreturn_amt)/sum(opening_bal.stock_qty+purchase_qty-purreturn_qty) as avg_cost,sum(purchase_amt+opening_bal.stock_amt-purreturn_amt)/sum(opening_bal.stock_qty+purchase_qty-purreturn_qty)*sales_qty as costofgoods,sales_amt-sum(purchase_amt+opening_bal.stock_amt-purreturn_amt)/sum(opening_bal.stock_qty+purchase_qty-purreturn_qty)*sales_qty as netprofit,
opening_bal.stock_amt/opening_bal.stock_qty*opening_bal.ending_inv as opening_amt,
opening_bal.ending_inv as opening_qty,
closing.purchase_amt,
closing.purreturn_amt,
closing.sales_amt,
closing.salesreturn_amt,
closing.sales_qty,
closing.salesreturn_qty,
closing.purchase_qty,
closing.purreturn_qty
from (select 'a' as join_id,sum(opening_stock_amt) as stock_amt,sum(opening_stock_qty) as stock_qty,
sum(opening_stock_qty+purchase_qty+salesreturn_qty-purreturn_qty-sales_qty) as ending_inv from
(select products.opening_stock_amt,products.opening_stock_qty,IFNULL(sales.sales_qty,0) as sales_qty,IFNULL(purchase_qty,0) as purchase_qty,IFNULL(purchase_amt,0) as purchase_amt,IFNULL(purreturn_qty,0) as purreturn_qty,IFNULL(purretun_amt,0) as purreturn_amt,IFNULL(salesreturn_qty,0) as salesreturn_qty,IFNULL(salesreturn_amt,0) as salesreturn_amt from
(select sum(qty) as sales_qty,'a' as join_id,sum(netamount) as sales_amt from customerbill where date(billdate)<sdate) sales
inner join (select sum(qty) as purchase_qty,'a' as join_id,sum(netamount) as purchase_amt from suplierbill where date(billdate)<sdate) purchase on sales.join_id=purchase.join_id
inner join (select sum(qty) as purreturn_qty,'a' as join_id,sum(netamount) as purretun_amt from rsuplierbill where date(billdate)<sdate) purreturn on purreturn.join_id=purchase.join_id
inner join (select sum(qty) as salesreturn_qty,'a' as join_id,sum(netamount) as salesreturn_amt from  scustomerbill where date(billdate)<sdate) salesreturn on purreturn.join_id=salesreturn.join_id
inner join (select sum(openingstock*basiccost) as opening_stock_amt,sum(openingstock) as opening_stock_qty,'a' as join_id from products) products on products.join_id=salesreturn.join_id) summary) opening_bal
inner join
(select 'a' as join_id,
IFNULL(purchase_qty,0) as purchase_qty,
IFNULL(purreturn_qty,0) as purreturn_qty,
IFNULL(sales_qty,0) as sales_qty,
IFNULL(salesreturn_qty,0) as salesreturn_qty,
IFNULL(purchase_amt,0) as purchase_amt,
IFNULL(purretun_amt,0) as purreturn_amt,
IFNULL(sales_amt,0) as sales_amt,
IFNULL(salesreturn_amt,0) as salesreturn_amt
from
(select sum(qty) as sales_qty,'a' as join_id,sum(netamount) as sales_amt from customerbill where date(billdate) between sdate and edate)
sales
inner join
(select sum(qty) as purchase_qty,'a' as join_id,sum(netamount) as purchase_amt from suplierbill where date(billdate) between sdate and edate)
purchase
on sales.join_id=purchase.join_id
inner join
(select sum(qty) as purreturn_qty,'a' as join_id,sum(netamount) as purretun_amt from rsuplierbill where date(billdate) between sdate and edate)
purreturn
on purreturn.join_id=purchase.join_id
inner join
(select sum(qty) as salesreturn_qty,'a' as join_id,sum(netamount) as salesreturn_amt from  scustomerbill where date(billdate) between sdate and edate)
salesreturn
on purreturn.join_id=salesreturn.join_id) closing  on
opening_bal.join_id=closing.join_id;
END |
