DROP PROCEDURE IF EXISTS `generate_income_statement`;
DELIMITER |
CREATE PROCEDURE `generate_income_statement`()
BEGIN
SELECT
concat(MONTHNAME(t.transaction_date)," - ",YEAR(CURDATE())) AS Month,
SUM(CASE WHEN a.group_id = 3 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END) AS 'Total Revenues',
SUM(CASE WHEN a.group_id = 4 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END) AS 'Total Expenses',
SUM(CASE WHEN a.group_id = 3 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END) - SUM(CASE WHEN a.group_id = 4 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END) AS 'Net Income'
FROM
transactions t
JOIN accounts a ON t.debit_account_id = a.account_id OR t.credit_account_id = a.account_id
WHERE
t.is_deleted<>1
AND YEAR(t.transaction_date) = YEAR(CURDATE())
GROUP BY
month
UNION ALL
SELECT
'Total',
SUM(CASE WHEN a.group_id = 3 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END),
SUM(CASE WHEN a.group_id = 4 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END),
SUM(CASE WHEN a.group_id = 3 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END) - SUM(CASE WHEN a.group_id = 4 AND YEAR(t.transaction_date) = YEAR(CURDATE()) THEN t.amount ELSE 0 END)
FROM
transactions t
JOIN accounts a ON t.debit_account_id = a.account_id OR t.credit_account_id = a.account_id
WHERE
t.is_deleted<>1
AND YEAR(t.transaction_date) = YEAR(CURDATE());
END |
DELIMITER ;

DROP PROCEDURE IF EXISTS `GetAccountSummaryInRange`;
DELIMITER |
CREATE PROCEDURE `GetAccountSummaryInRange`(IN StartDate DATE, IN EndDate DATE)
BEGIN
SELECT
ag.group_name AS Account_Group,
SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Debit,
SUM(CASE WHEN t.credit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Credit,
CONCAT(ABS(SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount WHEN t.credit_account_id = a.account_id THEN -t.amount ELSE 0 END)),
CASE WHEN SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount WHEN t.credit_account_id = a.account_id THEN -t.amount ELSE 0 END) < 0 THEN ' Cr' ELSE ' Dr' END) AS Balance
FROM
accounts a
JOIN
account_groups ag ON a.group_id = ag.group_id
LEFT JOIN
transactions t ON t.debit_account_id = a.account_id OR t.credit_account_id = a.account_id
WHERE
t.transaction_date BETWEEN StartDate AND EndDate
GROUP BY
ag.group_id;
SELECT
'Grand Total' AS Account_Group,
SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Debit,
SUM(CASE WHEN t.credit_account_id = a.account_id THEN t.amount ELSE 0 END) AS Credit,
CONCAT(ABS(SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount WHEN t.credit_account_id = a.account_id THEN -t.amount ELSE 0 END)),
CASE WHEN SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount WHEN t.credit_account_id = a.account_id THEN -t.amount ELSE 0 END) < 0 THEN ' Cr' ELSE ' Dr' END) AS Balance
FROM
accounts a
LEFT JOIN
transactions t ON t.debit_account_id = a.account_id OR t.credit_account_id = a.account_id
