bill
INNER JOIN (SELECT billno FROM customerbill WHERE is_deleted <> 1 AND warehouse_id = warehouseId) customerbill ON bill.billno = customerbill.billno
WHERE bill.is_deleted <> 1 -- Add the filter for is_deleted
GROUP BY
stockid
) bill ON p.autoid = bill.stockid
LEFT OUTER JOIN (SELECT
stockid,
IFNULL(SUM(qty), 0) AS purchase_qty
FROM
pbill
INNER JOIN (SELECT billno FROM suplierbill WHERE is_deleted <> 1 AND warehouse_id = warehouseId) suplierbill ON pbill.billno = suplierbill.billno
WHERE pbill.is_deleted <> 1 -- Add the filter for is_deleted
GROUP BY
stockid
) pbill ON p.autoid = pbill.stockid
LEFT OUTER JOIN (SELECT
stockid,
IFNULL(SUM(qty), 0) AS purreturn_qty
FROM
prbill
INNER JOIN (SELECT billno FROM rsuplierbill WHERE is_deleted <> 1 AND warehouse_id = warehouseId) rsuplierbill ON prbill.billno = rsuplierbill.billno
WHERE prbill.is_deleted <> 1 -- Add the filter for is_deleted
GROUP BY
stockid
) prbill ON p.autoid = prbill.stockid
LEFT OUTER JOIN (SELECT
stockid,
IFNULL(SUM(qty), 0) AS salesreturn_qty
FROM
sbill
INNER JOIN (SELECT billno FROM scustomerbill WHERE is_deleted <> 1 AND warehouse_id = warehouseId) scustomerbill ON sbill.billno = scustomerbill.billno
WHERE sbill.is_deleted <> 1 -- Add the filter for is_deleted
GROUP BY
stockid
) sbill ON p.autoid = sbill.stockid
LEFT OUTER JOIN (SELECT
stockid,
IFNULL(SUM(qty), 0) AS delivery_qty
FROM
delivery_bill
INNER JOIN (SELECT billno FROM delivery_summary WHERE is_deleted <> 1 AND warehouse_id = warehouseId) delivery_summary ON delivery_bill.billno = delivery_summary.billno
WHERE delivery_bill.is_deleted <> 1 -- Add the filter for is_deleted
GROUP BY
stockid
) delivery_bill ON p.autoid = delivery_bill.stockid
LEFT OUTER JOIN (SELECT
stockid,
IFNULL(SUM(qty), 0) AS received_qty
