DROP PROCEDURE IF EXISTS `GetProductStock`;
DELIMITER |
CREATE PROCEDURE `GetProductStock`(
IN product_id VARCHAR(255),
IN product_name VARCHAR(255),
IN limit_start INT,
IN limit_count INT
)
BEGIN
SELECT p.productid, p.description, p.MRP, p.retailprice, p.custom1, p.custom2, p.custom3, p.autoid,
(p.openingstock + IFNULL(purchase, 0) + IFNULL(salesreturn, 0) + IFNULL(received, 0) - IFNULL(sales, 0) - IFNULL(purreturn, 0) - IFNULL(delivered, 0) - IFNULL(out_qty, 0) + IFNULL(in_qty, 0)) AS stock, p.reorder
FROM (
SELECT autoid,productid, description, MRP, retailprice, custom1, custom2, custom3,openingstock, basiccost, reorder, IFNULL(prodqty, 0) AS 'in_qty', IFNULL(out_qty, 0) AS 'out_qty',
IFNULL(purchase_qty, 0) AS 'purchase', IFNULL(purreturn_qty, 0) AS 'purreturn', IFNULL(sales_qty, 0) AS 'sales',
IFNULL(SUM(salesreturn_qty), 0) AS 'salesreturn', IFNULL(SUM(received_qty), 0) AS 'received',
IFNULL(SUM(delivery_qty), 0) AS 'delivered'
FROM (
SELECT productid, description, MRP, retailprice, custom1, custom2, custom3, autoid, openingstock, basiccost, reorder
FROM products
WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
LIMIT limit_start, limit_count
) AS products
LEFT OUTER JOIN (
SELECT bill.stockid, IFNULL(SUM(bill.qty), 0) AS sales_qty
FROM (
SELECT billno,stockid, qty
FROM bill
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS bill
LEFT OUTER JOIN customerbill ON bill.billno = customerbill.billno AND customerbill.is_deleted <> 1
GROUP BY bill.stockid
) AS bill ON products.autoid = bill.stockid
LEFT OUTER JOIN (
SELECT pbill.stockid, IFNULL(SUM(pbill.qty), 0) AS purchase_qty
FROM (
SELECT billno,stockid, qty
FROM pbill
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS pbill
LEFT OUTER JOIN suplierbill ON pbill.billno = suplierbill.billno AND suplierbill.is_deleted <> 1
GROUP BY pbill.stockid
) AS pbill ON products.autoid = pbill.stockid
LEFT OUTER JOIN (
SELECT prbill.stockid, IFNULL(SUM(prbill.qty), 0) AS purreturn_qty
FROM (
SELECT billno,stockid, qty
FROM prbill
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS prbill
GROUP BY prbill.stockid
) AS prbill ON products.autoid = prbill.stockid
LEFT OUTER JOIN (
SELECT sbill.stockid, IFNULL(SUM(sbill.qty), 0) AS salesreturn_qty
FROM (
SELECT billno,stockid, qty
FROM sbill
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS sbill
GROUP BY sbill.stockid
) AS sbill ON products.autoid = sbill.stockid
LEFT OUTER JOIN (
SELECT delivery_bill.stockid, IFNULL(SUM(delivery_bill.qty), 0) AS delivery_qty
FROM (
SELECT billno,stockid, qty
FROM delivery_bill
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS delivery_bill
GROUP BY delivery_bill.stockid
) AS delivery_bill ON products.autoid = delivery_bill.stockid
LEFT OUTER JOIN (
SELECT received_bill.stockid, IFNULL(SUM(received_bill.qty), 0) AS received_qty
FROM (
SELECT billno,stockid, qty
FROM received_bill
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS received_bill
GROUP BY received_bill.stockid
) AS received_bill ON products.autoid = received_bill.stockid
LEFT OUTER JOIN (
SELECT stock_transfer.stockid, IFNULL(SUM(stock_transfer.in_qty), 0) AS in_qty
FROM (
SELECT stockid, in_qty
FROM stock_transfer
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS stock_transfer
GROUP BY stock_transfer.stockid
) AS stock_transfer ON products.autoid = stock_transfer.stockid
LEFT OUTER JOIN (
SELECT st.stockid, IFNULL(SUM(st.out_qty), 0) AS out_qty, IFNULL(SUM(st.in_qty), 0) AS prodqty
FROM (
SELECT stockid, out_qty, in_qty
FROM stock_transfer st
WHERE stockid IN (
SELECT autoid FROM products WHERE (productid = product_id OR description LIKE CONCAT('%', product_name, '%'))
AND description IS NOT NULL AND description <> ''
AND is_deleted <> 1
)
LIMIT limit_start, limit_count
) AS st
GROUP BY st.stockid
) AS st ON products.autoid = st.stockid
GROUP BY products.autoid
) AS p
WHERE (p.productid = product_id OR p.description LIKE CONCAT('%', product_name, '%'))
AND p.description IS NOT NULL AND p.description <> ''
LIMIT limit_start, limit_count;
END |
DELIMITER ;

