CASE WHEN ROUND(SUM(CASE WHEN t.debit_account_id = a.account_id THEN t.amount WHEN t.credit_account_id = a.account_id THEN -t.amount ELSE 0 END), 2) >= 0 THEN ' Dr' ELSE ' Cr' END
) AS Balance
FROM
accounts a
LEFT JOIN
