DROP PROCEDURE IF EXISTS `hsn_tax_summary`;
DELIMITER |
CREATE PROCEDURE `hsn_tax_summary`(
Operation varchar(20),
PSdate date,
PEdate date,
PTax tinyint
)
BEGIN
if Operation='Tax' then
select bill.billno,bill_date,customer.customername as name,customer.Tinno,Hsn_code,sum((billprice*bill.qty)-discount) as taxable_value,s_gst,sum(s_gst/100*(billprice*bill.qty-discount)) as
sgst_amount,c_gst,sum(c_gst/100*((billprice*bill.qty)-discount)) as cgst_amount,i_gst,sum(i_gst/100*((billprice*bill.qty)-discount)) as igst_amount,i_gst,sum(taxamount) as
taxamount from
(select billno,bill_date,taxamount,qty,billprice,discount,s_gst,c_gst,i_gst,stockid from bill where s_gst+c_gst+i_gst>0 and date(bill_date) between PSdate and PEdate) bill inner join (select autoid,hsn_code from products) products on bill.stockid=products.Autoid inner join
(select customerid,billno from customerbill where tax=PTax and date(billdate) between PSdate and PEdate) customerbill on bill.billno=customerbill.billno inner join (select customerid,customername,tinno from customer) customer on customer.customerid=customerbill.customerid
group by billno,products.hsn_code,s_gst,c_gst,i_gst;
