DROP PROCEDURE IF EXISTS `import_applicant_xml`;
DELIMITER |
CREATE PROCEDURE `import_applicant_xml`(IN xml_content text)
BEGIN
declare v_row_index int unsigned default 0;
declare v_row_count int unsigned;
declare v_xpath_row varchar(255);
declare node varchar(200);
set node = '/billing/bill';
set v_row_count  = extractValue(xml_content, concat('count(', node, ')'));
while v_row_index < v_row_count do
set v_row_index = v_row_index + 1;
set v_xpath_row = concat(node, '[', v_row_index, ']/@*');
`billprice`,
`qty`,
`discountpercentage`,
`discount`,
`s_gst`,
`c_gst`,
`i_gst`,
`taxamount`,
`stockid`,
`Netamount`,
`RefNo`,
`bill_date`)
values (
extractValue(xml_content, concat(v_xpath_row, '[1]')),
extractValue(xml_content, concat(v_xpath_row, '[2]')),
extractValue(xml_content, concat(v_xpath_row, '[3]')),
extractValue(xml_content, concat(v_xpath_row, '[4]')),
extractValue(xml_content, concat(v_xpath_row, '[5]')),
extractValue(xml_content, concat(v_xpath_row, '[6]')),
extractValue(xml_content, concat(v_xpath_row, '[7]')),
extractValue(xml_content, concat(v_xpath_row, '[8]')),
extractValue(xml_content, concat(v_xpath_row, '[9]')),
extractValue(xml_content, concat(v_xpath_row, '[10]')),
extractValue(xml_content, concat(v_xpath_row, '[11]')),
extractValue(xml_content, concat(v_xpath_row, '[12]')),
extractValue(xml_content, concat(v_xpath_row, '[13]'))
);
end while;
END |
