CREATE PROCEDURE `parent`()
BEGIN
SELECT
productid,
categoryname,
description,
openingstock,
basiccost,
uom,
unit,
CASE WHEN is_parent = 1 THEN unit ELSE NULL END AS unit_for_parent,  -- Include the unit only when is_parent is 1
IFNULL(in_qty, 0) AS 'in_qty',
IFNULL(out_qty, 0) AS 'out_qty',
IFNULL(purchase_qty, 0) AS 'purchase',
IFNULL(purreturn_qty, 0) AS 'purchasereturn',
IFNULL(sales_qty, 0) AS 'sales',
IFNULL(SUM(salesreturn_qty), 0) AS 'salesreturn',
IFNULL(SUM(received_qty), 0) AS 'received',
IFNULL(SUM(delivery_qty), 0) AS 'delivered'
FROM
(SELECT
autoid,
productid,
openingstock,
description,
basiccost,
uom,
unit,
is_parent,
categoryname
FROM
products
LEFT OUTER JOIN category ON products.category = category.CategoryId
WHERE
productid IN (SELECT productid FROM products WHERE is_parent = 1 AND is_deleted <> 1)
) products
LEFT OUTER JOIN
(SELECT
stockid,
IFNULL(SUM(qty), 0) AS sales_qty
FROM
bill
INNER JOIN
(SELECT billno FROM customerbill WHERE is_deleted <> 1) customerbill
ON
bill.billno = customerbill.billno
WHERE
bill.is_deleted <> 1
GROUP BY
stockid
) bill
ON
products.autoid = bill.stockid
LEFT OUTER JOIN
(SELECT
stockid,
IFNULL(SUM(qty), 0) AS purchase_qty
FROM
pbill
INNER JOIN
(SELECT billno FROM suplierbill WHERE is_deleted <> 1) suplierbill
ON
pbill.billno = suplierbill.billno
WHERE
pbill.is_deleted <> 1
GROUP BY
stockid
) pbill
ON
products.autoid = pbill.stockid
LEFT OUTER JOIN
(SELECT
stockid,
IFNULL(SUM(qty), 0) AS purreturn_qty
FROM
prbill
WHERE
prbill.is_deleted <> 1
GROUP BY
stockid
) prbill
ON
products.autoid = prbill.stockid
LEFT OUTER JOIN
(SELECT
stockid,
IFNULL(SUM(qty), 0) AS salesreturn_qty
FROM
sbill
WHERE
sbill.is_deleted <> 1
GROUP BY
stockid
) sbill
ON
products.autoid = sbill.stockid
LEFT OUTER JOIN
(SELECT
stockid,
IFNULL(SUM(qty), 0) AS delivery_qty
FROM
delivery_bill
