CREATE PROCEDURE `pro_customer_outstandings`()
BEGIN
SELECT
@row_number := @row_number + 1 AS Sno,
c.customername AS `Customer Name`,
c.CustomerMobile AS 'Mobile',
c.Accountid as 'Account Id',
c.Membership_Id as 'Membership Id',
FORMAT(ROUND(SUM(CASE WHEN t.debit_account_id = c.accountid THEN t.amount ELSE 0 END), 1), 2) AS Debit,
FORMAT(ROUND(SUM(CASE WHEN t.credit_account_id = c.accountid THEN t.amount ELSE 0 END), 1), 2) AS Credit,
CONCAT(
'₹',
FORMAT(
ABS(ROUND(SUM(CASE WHEN t.debit_account_id = c.accountid THEN t.amount ELSE -t.amount END), 1)), 2
),
IF(ROUND(SUM(CASE WHEN t.debit_account_id = c.accountid THEN t.amount ELSE -t.amount END), 1) < 0, ' Cr', ' Dr')
