DROP PROCEDURE IF EXISTS `sales_profit`;
DELIMITER |
CREATE PROCEDURE `sales_profit`(
IN sdate date,
IN edate date
)
BEGIN
select productid,category,description,opening_stock,opening_value,avg_cost,sales_qty,costofgoods,sales_amount,sales_amount-costofgoods as profit from
(select productid,category,opening_stock,opening_value,description,avg_cost,sales_qty,sales_amount,sales_qty*avg_cost as costofgoods from
(select productid,retailprice,categoryname as category,t1.description,opening_stock,opening_value,opening_value/opening_stock as avg_cost,ifnull(t1.sales_amount,0) as sales_amount,ifnull(t1.sales_qty,0) as sales_qty from
(select (ifnull(openingstock,0)+ifnull(purchase_qty,0)-ifnull(purreturn_qty,0)) as opening_stock,
(BasicCost*ifnull(openingstock,0)+ifnull(purchase_amount,0)-ifnull(purreturn_amount,0)) as opening_value,
autoid,productid,retailprice,description,categoryname from products
left outer join
category on products.category=category.CategoryId
left outer join
(select pbill.stockid,ifnull(sum(qty),0) as purchase_qty,ifnull(sum(netamount),0) as purchase_amount from pbill where date(bill_date)<=sdate group by pbill.stockid) pbill on products.autoid=pbill.stockid
left outer join
(select prbill.stockid,ifnull(sum(qty),0) as purreturn_qty,ifnull(sum(netamount),0) as purreturn_amount from prbill where date(bill_date)<=sdate group by prbill.stockid) prbill on products.autoid=prbill.stockid
) t
left outer join
(
select ifnull(sales_qty,0)-ifnull(salesreturn_qty,0) as sales_qty,ifnull(sales_amount,0)-ifnull(salesreturn_amount,0) as sales_amount,autoid,description from products
left outer join
(select bill.stockid,ifnull(sum(qty),0) as sales_qty,ifnull(sum(netamount),0) as sales_amount from bill where date(bill_date) between sdate and edate group by bill.stockid) bill
on products.autoid=bill.stockid
left outer join
(select sbill.stockid,ifnull(sum(qty),0) as salesreturn_qty,ifnull(sum(netamount),0) as salesreturn_amount from sbill where date(bill_date) between sdate and edate group by sbill.stockid) sbill
on products.autoid=sbill.stockid
)
t1 on t1.autoid=t.autoid) g)h;
END |
