DROP PROCEDURE IF EXISTS `sales_save`;
DELIMITER |
CREATE PROCEDURE `sales_save`(
IN billing_content text,
IN Account_content text,
IN customer_billno varchar(20),
IN customer_billdate datetime ,
IN customer_billstatus varchar(1),
IN customer_customerId varchar(15),
IN customer_orderNo varchar(10),
IN customer_shipto varchar(200),
IN customer_shippingcost smallint(6),
IN customer_amountTendered int,
IN customer_amountReturned int,
IN customer_seller_id tinyint,
IN customer_netamount float,
IN customer_qty float,
IN customer_tax float,
IN customer_paymentdate date,
IN invoice_no int,
IN invoice_ref tinyint
)
BEGIN
declare v_row_index int unsigned default 0;
declare v_row_count int unsigned;
declare v_xpath_row varchar(255);
declare node varchar(200);
declare result int unsigned;
DECLARE exit handler for sqlexception
BEGIN
ROLLBACK;
SHOW ERRORS LIMIT 1;
END;
START TRANSACTION;
set node = '/billing/bill';
set v_row_count  = extractValue(billing_content, concat('count(', node, ')'));
while v_row_index < v_row_count do
set v_row_index = v_row_index + 1;
set v_xpath_row = concat(node, '[', v_row_index, ']/@*');
`billprice`,
`qty`,
`discountpercentage`,
`discount`,
`s_gst`,
`c_gst`,
`i_gst`,
`taxamount`,
`stockid`,
`Netamount`,
`RefNo`,
`bill_date`)
values (
extractValue(billing_content, concat(v_xpath_row, '[1]')),
extractValue(billing_content, concat(v_xpath_row, '[2]')),
extractValue(billing_content, concat(v_xpath_row, '[3]')),
extractValue(billing_content, concat(v_xpath_row, '[4]')),
extractValue(billing_content, concat(v_xpath_row, '[5]')),
extractValue(billing_content, concat(v_xpath_row, '[6]')),
extractValue(billing_content, concat(v_xpath_row, '[7]')),
extractValue(billing_content, concat(v_xpath_row, '[8]')),
extractValue(billing_content, concat(v_xpath_row, '[9]')),
extractValue(billing_content, concat(v_xpath_row, '[10]')),
extractValue(billing_content, concat(v_xpath_row, '[11]')),
extractValue(billing_content, concat(v_xpath_row, '[12]')),
extractValue(billing_content, concat(v_xpath_row, '[13]'))
);
end while;
set node = '/billing/account_trans';
set v_row_count  = extractValue(Account_content, concat('count(', node, ')'));
set v_row_index=0;
while v_row_index < v_row_count do
set v_row_index = v_row_index + 1;
set v_xpath_row = concat(node, '[', v_row_index, ']/@*');
`debitid`,
`creditid`,
`Amount`,
`Vtype`,
`Narration`,
`Refno`,`seller_id`)
VALUES(
extractValue(Account_content, concat(v_xpath_row, '[1]')),
extractValue(Account_content, concat(v_xpath_row, '[2]')),
extractValue(Account_content, concat(v_xpath_row, '[3]')),
extractValue(Account_content, concat(v_xpath_row, '[4]')),
extractValue(Account_content, concat(v_xpath_row, '[5]')),
extractValue(Account_content, concat(v_xpath_row, '[6]')),
extractValue(Account_content, concat(v_xpath_row, '[7]')),
extractValue(Account_content, concat(v_xpath_row, '[8]')));
end while;
if invoice_ref=1 then
update bno set billno=invoice_no;
else
update ino set billno=invoice_no;
end if;
SELECT ROW_COUNT() INTO result;
IF (result>0) THEN
COMMIT;
else
SHOW ERRORS LIMIT 1;
ROLLBACK;
end if;
END |
DELIMITER ;

