DROP PROCEDURE IF EXISTS `trail_balance`;
DELIMITER |
CREATE PROCEDURE `trail_balance`(
IN `PSdate` date,
IN `PEdate` date
)
BEGIN
SELECT
opening_bal.accountid,
ROUND(opening_bal.opbalance + opening_bal.debit - opening_bal.credit, 2) AS openingbalance,
ROUND(accounts.debit, 2) AS debit,
ROUND(accounts.credit, 2) AS credit,
opening_bal.accountname
FROM
(
SELECT
accountid,
opbalance,
IFNULL(SUM(debit), 0) AS debit,
IFNULL(SUM(credit), 0) AS credit,
accountname
FROM
(
SELECT
AccountId,
opbalance,
amount AS debit,
0 AS credit,
accountname
FROM
account_group
LEFT OUTER JOIN
(
SELECT debitid, amount FROM `account_trans` WHERE DATE(trans_date) < PSdate
) account_trans ON account_group.accountid = account_trans.debitid
WHERE
accountgroup NOT IN (5, 6)
UNION ALL
SELECT
AccountId,
opbalance,
0 AS debit,
amount AS credit,
accountname
FROM
account_group
LEFT OUTER JOIN
(
SELECT creditid, amount FROM `account_trans` WHERE DATE(trans_date) < PSdate
) account_trans ON account_group.accountid = account_trans.creditid
WHERE
accountgroup NOT IN (5, 6)
) t
GROUP BY
AccountId
) opening_bal
INNER JOIN
(
SELECT
accountid,
IFNULL(SUM(debit), 0) AS debit,
IFNULL(SUM(credit), 0) AS credit,
accountname
FROM
(
SELECT
AccountId,
opbalance,
amount AS debit,
0 AS credit,
accountname
FROM
account_group
LEFT OUTER JOIN
(
SELECT debitid, amount FROM `account_trans` WHERE DATE(trans_date) BETWEEN PSdate AND PEdate
) account_trans ON account_group.accountid = account_trans.debitid
WHERE
accountgroup NOT IN (5, 6)
UNION ALL
SELECT
AccountId,
opbalance,
0 AS debit,
amount AS credit,
accountname
FROM
account_group
LEFT OUTER JOIN
(
SELECT creditid, amount FROM `account_trans` WHERE DATE(trans_date) BETWEEN PSdate AND PEdate
) account_trans ON account_group.accountid = account_trans.creditid
WHERE
accountgroup NOT IN (5, 6)
) t
GROUP BY
AccountId
) accounts ON accounts.accountid = opening_bal.accountid;
END |
DELIMITER ;

DROP PROCEDURE IF EXISTS `update_product_stock`;
DELIMITER |
CREATE PROCEDURE `update_product_stock`()
BEGIN
UPDATE products p
